mapper.insert:
INSERT INTO TB_BOX_MKT_PDF_CART_R
(
    PUCS_USIS_ID
    , PUCS_ID
    , PDF_INFO_ID
    , PDF_CNT
    , UT_ID
    , RGSN_TS
    , RGSN_USER_ID
    , AMNN_TS
    , AMNN_USER_ID
)
VALUES
(
    #{pucsUsisId}
    , #{pucsId}
    , #{pdfInfoId}
    , #{pdfCnt}
    , #{utId}
    , CURRENT_TIMESTAMP
    , #{rgsnUserId}
    , CURRENT_TIMESTAMP
    , #{amnnUserId}
)

mapper.update:
UPDATE
    TB_BOX_MKT_PDF_CART_R
SET
    PDF_CNT              = PDF_CNT + #{pdfCnt}
    , AMNN_TS            = CURRENT_TIMESTAMP
    , AMNN_USER_ID       = #{amnnUserId}
WHERE
    PUCS_USIS_ID = #{pucsUsisId}
    AND PUCS_ID = #{pucsId}
    AND PDF_INFO_ID = #{pdfInfoId}

mapper.delete:
DELETE FROM TB_BOX_MKT_PDF_CART_R
WHERE
    PUCS_USIS_ID = #{pucsUsisId}
    AND PUCS_ID = #{pucsId}
    AND PDF_INFO_ID = #{pdfInfoId}
